<form id="keyboardForm" class="hide">
	<div id="error" class="ui top attached label red" style="text-align: center;margin-top: 70px;display: none;">
	You have to choose one key at least
	</div>
	<div class="ui equal width form">
    	<div class="ui center aligned segment">
    		<div class="fields">
	    		<div class="field">
	    			<div class="ui icon labeled button keyButton" data-key="esc" key-id="">
	    				<i class="icon times circle"></i>ESC
	    			</div>
	    		</div>
	    		<div class="field">
	    			<div class="ui icon labeled button keyButton" data-key="space" key-id="">
	    				<i class="icon window minimize"></i>Space
	    			</div>
	    		</div>
    		</div>	
		  	<div class="ui horizontal divider">
				<i class="icon bolt teal" style="font-size: 30px;"></i>
		  	</div>
			<div class="fields">
				<div class="field">
					<div class="ui icon labeled button keyButton" data-key="left" key-id="">
						<i class="icon arrow circle left"></i>Left
					</div>
				</div>
				<div class="field">
					<div class="ui icon labeled button keyButton" data-key="right" key-id="">
						<i class="icon arrow circle right"></i>Right
					</div>
				</div>
				<div class="field">
					<div class="ui icon labeled button keyButton" data-key="up" key-id="">
						<i class="icon arrow circle up"></i>Up
					</div>
				</div>
				<div class="field">
					<div class="ui icon labeled button keyButton" data-key="down" key-id="">
						<i class="icon arrow circle down"></i>Down
					</div>
				</div>
			</div>
		</div>
    </div>
</form>


